import React, { useState } from 'react'
import {Container,Form,Button} from 'react-bootstrap'
import CreateQuestionForm from './CreateQuestion';
import {useNavigate} from 'react-router-dom'
function CreateExam() {
    const navigate = useNavigate();
    const [examName,setExamName] = useState("");
    const [state,setState] = useState(<CreateExam/>)

    const handleChange = (e) =>{
      setExamName(e.target.value)
    }

    const examHandler = async(e) => {
      e.preventDefault();
      const exam_name = examName;
      // console.log(exam_name)

      const res = await fetch('/api/exam/createExam',{
        method:"POST",
        headers:{
          "Content-Type" : "application/json"
        },
        body: JSON.stringify({exam_name})
      });

      // const data = await res.json();
      // console.log(res)
      if(res.status === 422 || !res){
        window.alert("error occured...")
      }else{
        console.log("called")
        setState(<CreateQuestionForm/>)
      }
    }

  return (
    <>
      <div>
        <Container>
          <Form className="registerform">
            <h1>Set Examination Name</h1>
            <h5>Please set a unique Examination name</h5>
            <Form.Group className="mb-3" controlId="formBasicName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="enter name"
                name='name'
                value={examName}
                onChange={handleChange}
              />
            </Form.Group>                        
  
            <Button variant="primary" onClick={examHandler}>
              Next
            </Button>
          </Form>
        </Container>
        </div>
        <div>
          {state == <CreateQuestionForm/> ? <CreateQuestionForm/> : null}
        </div>
    </>
  )
}

export default CreateExam
