import React, { useState } from 'react'
import {Container,Form,Button} from 'react-bootstrap'
import {loginUser} from './../Actions/userAction'
import {useSelector,useDispatch} from 'react-redux';
import Loader from '../Components/Loader';
import Success from '../Components/Success';
import Error from '../Components/Error';

function Login() {
  const dispatch = useDispatch();
  const {loading,success,error} = loginUser;

  const [user,setUser] = useState({
    registration_no:"",
    password:""
  });

  const handleChange = (e) => {
    setUser({...user,[e.target.name]:e.target.value})
  }
  return (
    <>
    <Container>
    {loading && <Loader/>}
    {success && <Success success="Student Registration Successful"/>}
    {error && <Error error={`${error.error}`} />}
      <Form className="registerform">
        <h1>Login</h1>
        
        <Form.Group className="mb-3" controlId="formBasicRegistratino_No">
          <Form.Label>Registration_No</Form.Label>
          <Form.Control
            type="number"
            placeholder="enter registration number"
            name='registration_no'
            value={user.registration_no}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            name='password'
            value={user.password}
            onChange={handleChange}
          />
        </Form.Group>
        

        <Button variant="primary" onClick={registerHandler}>
          Register
        </Button>
      </Form>
    </Container>
    </>
  )
}

export default Login
