import React, { useEffect, useState } from 'react';
import { Button, Table } from 'react-bootstrap';
import { getAllExams } from '../../Actions/examAction';
import { useSelector, useDispatch } from 'react-redux';
import Loader from '../Loader';
import Error from '../Error';
import { useNavigate } from 'react-router-dom';
import { getResultByUserIds } from '../../Actions/examAction';

function UserHome() {
  const dispatch = useDispatch();
  const examState = useSelector((state) => state.getExamReducer);
  const { loading, error, Allexam } = examState;
  const navigate = useNavigate();

  const [results, setResults] = useState({});

  const UserData = useSelector((state) => state.getResultByUserIdsReducer);

  const { Results } = UserData;
  console.log(Results);

  useEffect(() => {
    dispatch(getAllExams());
  }, [dispatch]);

  useEffect(() => {
    if (Allexam && Results) {
      const examResults = {};
      Allexam.forEach((exam) => {
        const result = Results.find((result) => result.exam_id === exam._id);
        examResults[exam._id] = result ? result.percentage : null;
      });
      setResults(examResults);
    }
  }, [Allexam, Results]);

  return (
    <>
      <div style={{ padding: '6rem' }}>
        {loading && <Loader />}
        {error && <Error error="Error While Fetching Users" />}
        <Table>
          <thead>
            <tr>
              <th>Exam Name</th>
              <th>Teacher Name</th>
              <th>Exam Date</th>
              <th>Start Exam</th>
              <th>Result</th>
            </tr>
          </thead>
          <tbody>
            {Allexam &&
              Allexam.map((exam) => (
                <tr key={exam._id}>
                  <td>{exam.exam_name}</td>
                  <td>{exam.teacher_name}</td>
                  <td>{exam.exam_date}</td>
                  <td>
                    {results[exam._id] === null ? (
                      <Button onClick={() => navigate(`/examination/${exam._id}`)}>Start</Button>
                    ) : (
                      'Completed'
                    )}
                  </td>
                  <td>{results[exam._id] === null ? 'Null' : results[exam._id]}</td>
                </tr>
              ))}
          </tbody>
        </Table>
      </div>
    </>
  );
}

export default UserHome;
