import React, { useState,useEffect } from 'react'
import {Container,Form,Button} from 'react-bootstrap'
import {loginUser} from './../Actions/userAction'
import {useSelector,useDispatch} from 'react-redux';
import Loader from '../Components/Loader';
import Success from '../Components/Success';
import Error from '../Components/Error';

function Login() {

  const loginState = useSelector((state)=>state.loginUserReducer)
  const {loading,success,error} = loginState; 
  const token = localStorage.getItem('userData');
  console.log(token);
  
  const dispatch = useDispatch();
  
  const [user,setUser] = useState({
    registration_no:"",
    password:""
  });

  const [currentUser, setCurrentUser] = useState(null);
  const [route,setRoute] = useState(null);
///////////////// fetching user data ////////////////////////
  const fetchUserData = async () => {
    try {
      const response = await fetch('/api/user/rootUser', {
        method: 'GET',
      });
      const data = await response.json();
      setCurrentUser(data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleChange = (e) => {
    setUser({...user,[e.target.name]:e.target.value})
  }

  const loginHandler = async(e) =>{
    e.preventDefault();
    const {registration_no,password} = user;
    if(registration_no==null || password==null){
      window.alert("Please fill the fields correctly..");
    }else{
      const userdata = {registration_no,password};
      await dispatch(loginUser(userdata));
    }
  }
  /////////////////////////            /////////////////
  useEffect(() => {
    if (token) {
      fetchUserData();
    }
  }, [token]);

  const getroute=()=>{
    if (currentUser === null) {
      return;
    } else if (currentUser && currentUser.isAdmin !== undefined && currentUser.isAdmin) {
      console.log('cll');
      return 'admin/dashboard';
    } else {
      console.log('f');
      return 'home';
    }
  }
  

  return (
    <>
    <Container>
    {loading && <Loader/>}
    {success && <Success success="Successfully Login" route={getroute()}/>}
    {error && <Error error={`${error.message}`} />}
      <Form className="registerform">
        <h1>Login</h1>
        
        <Form.Group className="mb-3" controlId="formBasicRegistratino_No">
          <Form.Label>Registration_No</Form.Label>
          <Form.Control
            type="number"
            placeholder="enter registration number"
            name='registration_no'
            value={user.registration_no}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            name='password'
            value={user.password}
            onChange={handleChange}
          />
        </Form.Group>
        

        <Button variant="primary" onClick={loginHandler}>
          Login
        </Button>
      </Form>
    </Container>
    </>
  )
}

export default Login
