import React, { useEffect, useState } from 'react';
import { Button, Modal, Row } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { getAllQuestions } from '../../Actions/examAction';

function ResultData({ student, percentage, exam_id, answers }) {
  const dispatch = useDispatch();
  const QuestionsData = useSelector((state) => state.getAllQuestionsReducer);
  const { Questions, loading, error } = QuestionsData;
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(() => {
    dispatch(getAllQuestions(exam_id));
  }, [exam_id, dispatch]);

  return (
    <>
      <tr>
        <td>{student.name}</td>
        <td>{student.registration_no}</td>
        <td>{percentage}</td>
        <td>
          <Button onClick={handleShow}>Review</Button>
        </td>
      </tr>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {Questions &&
            Questions[0].questions.map((question, index) => {
              const correctAnswer = question.answer;
              const answeredOption = answers[index];

              return (
                <div key={index}>
                  <Row>
                    <h4>({index + 1}): {question.title}</h4>
                    {question.options.map((option, optionIndex) => {
                      const isCorrectAnswer = option === correctAnswer;
                      const isSelectedAnswer = option === answeredOption;

                      let optionStyle = {};
                      if (isCorrectAnswer) {
                        optionStyle = { color: 'green' };
                      } else if (!isCorrectAnswer && isSelectedAnswer) {
                        optionStyle = { color: 'red' };
                      }

                      return (
                        <div key={optionIndex}>
                          <h5 style={optionStyle}>{option}</h5>
                        </div>
                      );
                    })}
                  </Row>
                </div>
              );
            })}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default ResultData;
