import React, { useState, useEffect } from 'react';
import { Link, Prompt, useNavigate } from 'react-router-dom';
import UserHome from '../Components/User/UserHome';

function HomeScreen() {
  const data = sessionStorage.getItem('userData');
  const [currentUser, setCurrentUser] = useState(null);
  const [showPrompt, setShowPrompt] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (data) {
      setCurrentUser(JSON.parse(data));
    }
  }, [data]);

  useEffect(() => {
    const handleBeforeUnload = (event) => {
      event.preventDefault();
      event.returnValue = '';
    };

    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, []);

  const handlePrompt = (location) => {
    if (location.action === 'POP') {
      setShowPrompt(true);
    }
  };

  const handleConfirm = () => {
    sessionStorage.clear();
    navigate('/');
  };

  const handleCancel = () => {
    setShowPrompt(false);
  };

  return (
    <div>
      <Prompt when={showPrompt} message="Are you sure you want to exit the website?" />
      {currentUser !== null ? (
        <div>
          {currentUser.isAdmin === true ? (
            <h4>
              Go To Admin Panel <Link to="/admin/dashboard">click me!</Link>
            </h4>
          ) : (
            <UserHome />
          )}
        </div>
      ) : (
        <div>
          <h1>Welcome to VSSUT, MCA Exam Portal</h1>
          <h4>Please Login to continue...</h4>
        </div>
      )}
      {showPrompt && (
        <div>
          <p>Are you sure you want to exit the website?</p>
          <button onClick={handleConfirm}>Yes</button>
          <button onClick={handleCancel}>Back</button>
        </div>
      )}
    </div>
  );
}

export default HomeScreen;
