import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import UserHome from '../Components/User/UserHome';

function HomeScreen() {
  const data = sessionStorage.getItem('userData');
  const [currentUser, setCurrentUser] = useState(null);
  const [showAlert, setShowAlert] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    if (data) {
      setCurrentUser(JSON.parse(data));
    }
  }, [data]);

  useEffect(() => {
    const handleBeforeUnload = (event) => {
      event.preventDefault();
      event.returnValue = '';
    };

    const handleNavigate = (location, action) => {
      if (action === 'POP') {
        setShowAlert(true);
      }
    };

    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, []);

  const handleConfirm = () => {
    sessionStorage.clear();
    navigate('/');
  };

  const handleCancel = () => {
    setShowAlert(false);
  };

  useEffect(() => {
    const unblock = navigate(location.pathname, { replace: true });

    return () => {
      unblock();
    };
  }, [location, navigate]);

  return (
    <div>
      {currentUser !== null ? (
        <div>
          {currentUser.isAdmin === true ? (
            <h4>
              Go To Admin Panel <Link to="/admin/dashboard">click me!</Link>
            </h4>
          ) : (
            <UserHome />
          )}
        </div>
      ) : (
        <div>
          <h1>Welcome to VSSUT, MCA Exam Portal</h1>
          <h4>Please Login to continue...</h4>
        </div>
      )}

      {showAlert && (
        <div>
          <p>Are you sure you want to exit the website?</p>
          <button onClick={handleConfirm}>Yes</button>
          <button onClick={handleCancel}>Back</button>
        </div>
      )}
    </div>
  );
}

export default HomeScreen;
