import React,{useState,useEffect} from 'react'
import { Link } from 'react-router-dom';
import UserHome from '../Components/User/UserHome';

function HomeScreen() {

  const data = sessionStorage.getItem('userData')

  const [currentUser, setCurrentUser] = useState(null);
  // console.log(currentUser)

  useEffect(()=>{
    if(data){
      setCurrentUser(JSON.parse(data))
    }
  },[data])

  const handleDeleteData = () => {
    sessionStorage.removeItem('userData');
    window.location.href = 'chrome://newtab';
  };

  useEffect(() => {
    const confirmDelete = (event) => {
      event.preventDefault();
      event.returnValue = '';
    };
  
    const handleBeforeUnload = () => {
      window.removeEventListener('beforeunload', confirmDelete);
    };
  
    window.addEventListener('beforeunload', confirmDelete);
  
    return handleBeforeUnload;
  }, []);
  

  return (
    <>
     <div>
     {currentUser !==null? 
      (
        <div>
          {/* <h1>Welcome {currentUser.name}</h1> */}
          {currentUser.isAdmin===true ? (<h4>Go To Admin Pannel <Link to={"/admin/dashboard"}>click me!</Link></h4>) : <UserHome/> }
        </div>
      ) 
      :
       (<div>
        <h1>Welcome to VSSUT, MCA Exam Protal</h1>
      <h4>Please Login to continue...</h4>
      </div>) }
     </div>
     {currentUser !== null && (
      <button onClick={handleDeleteData}>Delete Data and Exit</button>
    )}
    </>
  )
}

export default HomeScreen
