import { useEffect } from 'react';
import {BrowserRouter,Route,Routes} from 'react-router-dom'
import './App.css';
import Login from './Screens/Login';
import Register from './Components/Admin/Register';
import LoadingScreen from './Screens/LoadingScreen';
import HomeScreen from './Screens/HomeScreen';
import {useDispatch,useSelector} from 'react-redux'
import AllStudents from './Components/Admin/AllStudents';
import CreateQuestionForm from './Components/Admin/CreateQuestion';
import CreateExam from './Components/Admin/CreateExam';
import AdminScreen from './Screens/AdminScreen';

function App() {
  const dispatch = useDispatch();
  const isLoading = useSelector(state => state.loadingReducer);
  useEffect(() => {
    // Simulate a loading delay
    const timeout = setTimeout(() => {
      dispatch({ type: 'SET_LOADING', payload: false });
    }, 5000);

    return () => {
      clearTimeout(timeout);
    };
  }, [dispatch]);

  return (
    <>
      <LoadingScreen/>
      {!isLoading && 
        <BrowserRouter>
        <HomeScreen/>
        <Routes>
          <Route exact path='/admin' element={<AdminScreen/>}>
            <Route exact path='/admin/userlist' element={<AllStudents/>}/>
            <Route exact path='/admin/createExam' element= {<CreateExam/>} />
            <Route exact path='/admin/createQuestions' element={<CreateQuestionForm/>}  />
          </Route>
          <Route exact path='/login' element={<Login/>}/>
          <Route exact path='/register' element={<Register/>}/>
        </Routes>
      </BrowserRouter>
      }

    </>
  );
}

export default App;
