import React,{useEffect, useState} from 'react'
import { Button,Modal, Row } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux';
import { getAllQuestions } from '../../Actions/examAction';

function ResultData({student,percentage,exam_id}) {
    const dispatch = useDispatch();
    const QuestionsData = useSelector((state)=> state.getAllQuestionsReducer)
    const {Questions,loading,error} = QuestionsData;
    // console.log(student)
    console.log(Questions[0].questions[0])
    const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(()=>{
    dispatch(getAllQuestions(exam_id));
  },[exam_id,dispatch])
  return (
    <>
       <tr>
          <td>{student.name}</td>
          <td>{student.registration_no}</td>
          <td>{percentage}</td>
          <td><Button onClick={handleShow}>Review</Button></td>
        </tr>

        <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* {Questions && Questions.map((question)=>(
            <div>
              <Row>
                <h4>{question.title}</h4>
                {question.options.map((option)=>(
                  <h6>{option}</h6>
                ))}
              </Row>
            </div>
          ))} */}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  )
}

export default ResultData
