import React, { useState, useEffect } from 'react';
import { useSelector,useDispatch } from 'react-redux'; 
import { getAllQuestions, getExamById } from '../../Actions/examAction';
import { useParams } from 'react-router-dom';



const ExaminationPage = () => {
  const dispatch = useDispatch();
  const AllQuestions = useSelector((state)=>state.getAllQuestionsReducer);
  const {Questions} = AllQuestions;
  const getExam = useSelector((state)=> state.getExamByIdReducer)
  const {Allexam} = getExam;
  // console.log(Allexam)
  const[isOn,setIsOn] = useState(false);
  const[questions,setQuestions] = useState([]);
  const[answer,setAnswer] = useState([]);
  const[currentIndex,setCurrentIndex] = (0)
  const examId = useParams();
  // console.log(examId);

  useEffect(()=>{
    dispatch(getAllQuestions(examId))
    dispatch(getExamById(examId))
  },[dispatch,examId])

  useEffect(()=>{
    if(Allexam && Questions){
      setIsOn(Allexam.exam_type)
      setQuestions(Questions[0].questions);
      setAnswer(new Array(Questions.length).fill(null));
    }
  },[Allexam,Questions]);

   console.log(questions)
  return (
   <>
   {
    isOn!==true ? (
      <div>
        {questions && questions.map((quesion,questionIndex)=>(
          <div>
          <h3>{quesion[questionIndex].title}</h3>
          {quesion[questionIndex].options.map((option,optionIndex)=>(
            <label key={optionIndex}>
              <input
                type='radio'
                name='option'
                value={optionIndex}
                checked={answer[currentIndex] === optionIndex}
              />
            </label>
          ))}
          </div>
          
        ))}
      </div>
    ) : (<div></div>)
   }

   </>
  );
};

export default ExaminationPage;
