    // }catch(err){
    //     dispatch({type:"USER_REGISTER_FAIL",payload:err})
    // }
    const successHandler = () =>{
        dispatch({type:"USER_REGISTER_SUCCESS"})
        
        setTimeout(()=>{
            navigate('/login')
        },5000)
    }

     return await axios.post('/api/user/addUser',user).then(successHandler).catch(error => {
        // Deal with the error
        dispatch(errorActionCreator("CREATE_ARTICLE_ERROR", error))
      })
         
        

}

export const errorActionCreator = (errorType, error) => {
    return {
      type: errorType,
      error: true,
      payload: error,
    }
  }
