import { useEffect,useState } from 'react';
import {BrowserRouter,Route,Routes} from 'react-router-dom'
import './App.css';
import Login from './Screens/Login';
import Register from './Components/Admin/Register';
import LoadingScreen from './Screens/LoadingScreen';
import {useDispatch,useSelector} from 'react-redux'
import AllStudents from './Components/Admin/AllStudents';
import CreateQuestionForm from './Components/Admin/CreateQuestion';
import CreateExam from './Components/Admin/CreateExam';
import AdminScreen from './Screens/AdminScreen';
import NavBar from './Components/NavBar';
import AdminRoute from './AdminRoute';

function App() {
  const dispatch = useDispatch();
  const isLoading = useSelector(state => state.loadingReducer);
  const loginstate= useSelector((state)=>state.loginUserReducer);
  const {token} = loginstate;
  const [currentUser,setCurrentUser] = useState();
  const [isAdmin,setIsAdmin] = useState();

  const userData = async()=>{
    const res = await fetch('/api/user/rootUser',{
      method:"GET"
    }).then((r)=>{return r.json()}).catch(err=>console.log(err))
    setCurrentUser(res)
  }

  useEffect(() => {
    if(token){
      userData();
    }
    // Simulate a loading delay
    const timeout = setTimeout(() => {
      dispatch({ type: 'SET_LOADING', payload: false });
    }, 5000);

    return () => {
      clearTimeout(timeout);
    };
  }, [dispatch,token]);

  if(!currentUser){
    console.log("wait");
  }else{
    const Admin = currentUser.isAdmin
    setIsAdmin(Admin)
  }

  return (
    <>
      <LoadingScreen/>
      {!isLoading && 
        <BrowserRouter>
        <NavBar/>
        <Routes>
          <Route  path='/admin' element={<AdminRoute component={<AdminScreen/>} isAdmin={isAdmin} />}>
             {/* <Route  path='/' element={<AdminScreen/>} />  */}
            <Route  path='/admin/userlist' element={<AllStudents/>}/>
            <Route  path='/admin/createExam' element= {<CreateExam/>} />
            <Route  path='/admin/createQuestions' element={<CreateQuestionForm/>}  />
          </Route>
          <Route exact path='/login' element={<Login/>}/>
          <Route exact path='/register' element={<Register/>}/>
        </Routes>
      </BrowserRouter>
      }

    </>
  );
}

export default App;
