import React, { useEffect, useState } from 'react'
import {Navbar,Container,Nav,NavDropdown} from 'react-bootstrap'
import {useSelector,useDispatch} from 'react-redux'
import {LinkContainer} from 'react-router-bootstrap'
import { logoutUser,getRootUser } from '../Actions/userAction'

function NavBar() {
  const dispatch = useDispatch();
    const authState = useSelector((state)=>state.authReducer);
    const {isAuthenticated} = authState;
     const loginState = useSelector((state)=>state.getRootUserReducer);
     const {currentUser} = loginState
    console.log(currentUser)
  //  console.log(token)
  useEffect(()=>{
    const token = localStorage.getItem('userData')
    console.log(token)
    dispatch(getRootUser(token))
  },[dispatch])
  return (
    <>
         <Navbar bg="light" expand="lg">
      <Container>
        <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            {
                isAuthenticated ? (
                    <div>
                      <LinkContainer to="/">
                      {/* <Nav.Link href="#features">{currentUser.name}</Nav.Link> */}
                        <NavDropdown title={"profile"} id="basic-nav-dropdown">
                          <h5>{currentUser.name}</h5>
                            <LinkContainer to="/orders">
                              <NavDropdown.Item>profile</NavDropdown.Item>
                            </LinkContainer>
                            <NavDropdown.Item
                              onClick={() => {
                                dispatch(logoutUser());
                              }}
                            >
                              Logout
                            </NavDropdown.Item>
                          </NavDropdown>
                      </LinkContainer>
                    </div>
                ):(
                    <LinkContainer to="/login">
                    <Nav.Link href="#features">Login</Nav.Link>
                    </LinkContainer>
                )
            }
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
    </>
  )
}

export default NavBar
