import React, { useEffect,useState } from 'react'
import { Navigate, Outlet } from 'react-router-dom'
import { useSelector,useDispatch } from 'react-redux';
import { getRootUser } from './Actions/userAction';
 function PrivateRoutes () {
  const loginstate= useSelector((state)=>state.loginUserReducer);
  const {token} = loginstate;
    const [currentUser,setCurrentUser] = useState();
//  console.log(token)
// console.log(currentUser)
const userData = async()=>{
  const res = await fetch('/api/user/rootUser',{
    method:"GET"
  }).then((r)=>{return r.json()}).catch(err=>console.log(err))
  setCurrentUser(res)
}

useEffect(()=>{
  // console.log(token)
  if(token){
    userData();
  }
},[token])

    // const isAdmin = true;
  if(currentUser&& currentUser.isAdmin !== undefined && currentUser.isAdmin){
    return <Outlet/>
  }else if (currentUser=== null) {
    // Handle case where user data is still loading or not available
    return <div>Loading...</div>;
  } 
  else{
    return <Navigate to={"/"}/>
  }
}

export default PrivateRoutes
