import React,{useEffect} from 'react'
import { Button, Table } from 'react-bootstrap'
import { getAllExams,getResultByUserId } from '../../Actions/examAction';
import { useSelector,useDispatch } from 'react-redux';
import Loader from '../Loader';
import Error from '../Error';
import { useNavigate } from 'react-router-dom';
import ExaminationPage from './ExaminationPage';

function UserHome() {
    const dispatch = useDispatch();
    const examState = useSelector((state)=>state.getExamReducer)
    const {loading,error,Allexam} = examState;
    const navigate = useNavigate();

    const result = async(exam_id) =>{
        const res =await fetch('/api/result/getResultByUserId',{
            method:"POST",
            headers:{
              "Content-Type" : "application/json"
            },
            body: JSON.stringify({exam_id})
          }).then((data)=> data.json());
          console.log(res)
          return res.percentage;
    }
    useEffect(()=>{
        dispatch(getAllExams());
    },[dispatch])

  return (
    <>
        <div style={{padding:'6rem'}}>
        {loading && <Loader/>}
      {error && <Error error="Error While Fetching Users" />}
            <Table>
                <thead>
                    <tr>
                        <th>Exam Name</th>
                        <th>Teacher Name</th>
                        <th>Exam Date</th>
                        <th>Start Exam</th>
                        <th>Result</th>            
                    </tr>
                </thead>
                <tbody>
                    {Allexam && 
                    Allexam.map((exam)=>(
                        <tr key={exam._id}>
                            <td>{exam.exam_name}</td>
                            <td>{exam.teacher_name}</td>
                            <td>{exam.exam_date}</td>
                            <td><Button onClick={()=>{navigate(`/examination/${exam._id}`)}}>Start</Button></td>
                            <td>{result(exam._id)}</td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div>
    </>
  )
}

export default UserHome
