import React, { useEffect } from 'react'
import { Navigate, Outlet } from 'react-router-dom'
import { useSelector,useDispatch } from 'react-redux';
import { getRootUser } from './Actions/userAction';
function PrivateRoutes() {
    const dispatch = useDispatch();
    const UserData = useSelector((state)=>state.getRootUserReducer);
    const token = localStorage.getItem('userData')
    console.log(token)
    let {currentUser} = UserData; 

    console.log(currentUser);

    useEffect(()=>{
        if(token){
           dispatch(getRootUser());
          }
    },[dispatch])

    // const isAdmin = true;
  if(currentUser && currentUser.isAdmin !== undefined && currentUser.isAdmin){
    return <Outlet/>
  }else if (currentUser === null) {
    // Handle case where user data is still loading or not available
    return <div>Loading...</div>;
  } 
  else{
    return <Navigate to={"/"}/>
  }
}

export default PrivateRoutes
