export const registerUserReducer = (state={},action) =>{
    switch(action.type){
        case 'USER_REGISTER_REQUEST':
            return{
                ...errorReducer(state, action),
                loading: true
            }
        case 'USER_REGISTER_SUCCESS':
            return{
                loading : false,
                success : true
            }
        case 'USER_REGISTER_FAIL':
            return{
                ...errorReducer(state, action),
                loading: false,
                // error: action.payload
                
            }
        default:
            return {state}        
    }
}

export const loginUserReducer = (state={success:false},action) =>{
    switch(action.type){
        case 'USER_LOGIN_REQUEST' :
            return{
                ...errorReducer(state,action),
                loading:true
            }
        case 'USER_LOGIN_SUCCESS':
            return{
                loading:false,
                success:true,
                token: action.payload
            }
        case 'USER_LOGIN_FAIL' :
            return{
                ...errorReducer(state,action),
                loading:false
            }
        case 'RESET_SUCCESS':
            return {
                ...state,
                success: false,
            };
        default : 
            return state;
    }
};

export const logoutUserReducer = (state={},action) =>{
    switch(action.type){
        case 'USER_LOGOUT_REQUEST' :
            return{
                ...errorReducer(state,action),
                loading:true
            }
        case 'USER_LOGOUT_SUCCESS':
            return{
                loading:false,
                success:true
            }
        case 'USER_LOGOUT_FAIL' :
            return{
                ...errorReducer(state,action),
                loading:false
            }
        default : 
            return state;
    }
};

export const authReducer = (state = {isAuthenticated:false}, action) => {
    switch (action.type) {
      case 'LOGIN_SUCCESS':
        return {
          ...state,
          isAuthenticated: true
        };
      case 'LOGOUT':
        return {
          ...state,
          isAuthenticated: false
        };
      default:
        return state;
    }
  };

export const getAllUserReducer = (state={users:[]},action) =>{
    switch(action.type){
        case 'GET_USERS_REQUEST' :
            return{
                ...errorReducer(state,action),
                loading:true
            }
        case 'GET_USERS_SUCCESS':
            return{
                users: action.payload,
                loading:false
            }
        case 'GET_USERS_FAIL' :
            return{
                ...errorReducer(state,action),
                loading:false
            }
        default : 
            return state;
    }
}

export const getUsersByIdReducer = (state={students:[]},action) =>{
    switch(action.type){
        case 'GETUSERSBYID_REQUEST' :
            return{
                ...errorReducer(state,action),
                loading:true
            }
        case 'GETUSERSBYID_SUCCESS':
            return{
                students: action.payload,
                loading:false
            }
        case 'GETUSERSBYID_FAIL' :
            return{
                ...errorReducer(state,action),
                loading:false
            }
        default : 
            return state;
    }
}

