import React from 'react';
import { useSelector } from 'react-redux';

const LoadingScreen = () => {
  const isLoading = useSelector(state => state.loadingReducer);

  if (isLoading) {
    return <div style={{height:"100vh",width:"100vw",color:"white",backgroundColor:"black"}}>Loading...</div>; // Replace with your desired loading screen content
  }

  return null;
};

export default LoadingScreen;
