import React, { useState, useEffect } from 'react';
import { useSelector,useDispatch } from 'react-redux'; 
import { getAllQuestions, getExamById } from '../../Actions/examAction';
import { useParams } from 'react-router-dom';



const ExaminationPage = () => {
  const [isOn, setIsOn] = useState(false);
  const [fetchQuestions, setFetchQuestions] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [showResult, setShowResult] = useState(false);
  const dispatch = useDispatch();
  const allQuestions = useSelector((state)=>state.getAllQuestionsReducer);
  const Questions = {allQuestions} 
  
  const examId = useParams();
//   useEffect(() => {
//     // Fetch isOn value from the database or any other data source
//     // and set the initial value of isOn accordingly
//     const isOnValue = // Fetch isOn value from the database
//     setIsOn(isOnValue);
//   }, []);

  useEffect(() => {
    if (!isOn) {
      const timer = setTimeout(() => {
        setFetchQuestions(true);
      }, 10000); // Fetch questions after 10 seconds (10000 milliseconds)

      return () => {
        clearTimeout(timer);
      };
    }
  }, [isOn]);

  useEffect(() => {
    if (fetchQuestions) {
      const timer = setInterval(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % questions.length);
      }, 10000); // 10 seconds (10 * 1000 milliseconds)

      return () => {
        clearInterval(timer);
      };
    } else {
      const timer = setTimeout(() => {
        setShowResult(true);
      }, 300000); // Submit form after 5 minutes (5 * 60 * 1000 milliseconds)

      return () => {
        clearTimeout(timer);
      };
    }
  }, [fetchQuestions, questions]);

  const handleOptionSelect = (optionIndex) => {
    const updatedAnswers = [...answers];
    updatedAnswers[currentIndex] = optionIndex;
    setAnswers(updatedAnswers);
  };

  const handleSubmit = () => {
    setShowResult(true);
  };

  const calculateCorrectPercentage = () => {
    const totalQuestions = questions.length;
    const correctAnswers = answers.filter(
      (answer, index) => answer === questions[index].answer
    ).length;
    const percentage = (correctAnswers / totalQuestions) * 100;
    return percentage.toFixed(2);
  };

  if(Questions){
    setQuestions(Questions);
    setAnswers(new Array(Questions.length).fill(null));
  }

  useEffect(() => {
    if (fetchQuestions) {
      dispatch(getAllQuestions(examId))
      dispatch(getExamById(examId))
    }
  }, [fetchQuestions,dispatch,examId]);

  return (
    <div>
      {/* <label>
        <input
          type="checkbox"
          checked={isOn}
          onChange={() => setIsOn(!isOn)}
        />
        Fetch Questions
      </label> */}
      {fetchQuestions && questions.length > 0 ? (
        <div>
          <h3>{questions[currentIndex].question}</h3>
          {questions[currentIndex].options.map((option, optionIndex) => (
            <label key={optionIndex}>
              <input
                type="radio"
                name={`question-${currentIndex}`}
                value={optionIndex}
                checked={answers[currentIndex] === optionIndex}
                onChange={() => handleOptionSelect(optionIndex)}
              />
              {option}
            </label>
          ))}
        </div>
      ) : null}
      {showResult && (
        <div>
          <h3>Result:</h3>
          <p>Correct Percentage: {calculateCorrectPercentage()}%</p>
        </div>
      )}
      {!isOn && (
        <button onClick={handleSubmit}>Submit</button>
      )}
    </div>
  );
};

export default ExaminationPage;
