import React, { useEffect,useState } from 'react'
import { useSelector,useDispatch } from 'react-redux'; 
import { getResultByExamId } from '../../Actions/examAction';
import { getUsersById } from '../../Actions/userAction';
import Loader from '../Loader';
import Error from '../Error';
import { Button, Table,Modal } from 'react-bootstrap';

function Results(exam_id) {
    const dispatch = useDispatch();
    const resultdata = useSelector((state)=>state.getResultByExamIdReducer)
    const {Result }= resultdata;
    const userData  = useSelector((state)=>state.getUsersByIdReducer);
    const {students,loading,error} = userData;
    const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = (res) => {
    modal(res)
    setShow(true)
  };
    // console.log(Result);
  //  console.log(students)
    useEffect(()=>{
       dispatch( getResultByExamId(exam_id))
    },[]);

    /////////////////// Getting the users by sending array of ids//////////////
    useEffect(()=>{
      if(Result!=null){
        const user_ids = Result.map((result)=> result.user_id)
        // console.log(user_ids)
         dispatch(getUsersById(user_ids));
      }
    },[Result]);

    const modal = (res) =>{
      return(
        <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>{res.percentage}</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
      )
    }

  return (
    <>
      <div>
        {loading && <Loader/>}
        {error && <Error/>}

        <div>
        <Table>
          <thead>
            <tr>
              <th>Name Of Student</th>
              <th>Registration No</th>
              <th>Mark</th>
              <th>Review Answers</th>
            </tr>
          </thead>
          <tbody>
            {
              Result && students && students.map((student)=>{
                const res = Result.find((result)=> { return result.user_id === student._id})
                const percentage = res.percentage;
                
                return(
                  <tr>
                  <td>{student.name}</td>
                  <td>{student.registration_no}</td>
                  <td>{percentage}</td>
                  <td><Button onClick={()=>handleShow(res)}>Review</Button></td>
                </tr>
                )
            })
            }
          </tbody>
                
        </Table>         
        </div>
        
      </div> 
    </>
  )
}

export default Results
