import React, { useState } from 'react'
import {Container,Form,Button,ButtonGroup,ToggleButton} from 'react-bootstrap'
import CreateQuestionForm from './CreateQuestion';

function CreateExam() {
    const [examName,setExamName] = useState("");
    const [show,setShow] = useState(false)
    const [radioValue, setRadioValue] = useState('1');
    const radios = [
      { name: 'Active', value: '1' },
      { name: 'Radio', value: '2' },
      { name: 'Radio', value: '3' },
    ];

    const handleChange = (e) =>{
      setExamName(e.target.value)
    }

    const examHandler = async(e) => {
      e.preventDefault();
      const exam_name = examName;
      // console.log(exam_name)

      const res = await fetch('/api/exam/createExam',{
        method:"POST",
        headers:{
          "Content-Type" : "application/json"
        },
        body: JSON.stringify({exam_name})
      });

      // const data = await res.json();
      // console.log(res)
      if(res.status === 400 || res.status ===422 || !res){
        window.alert("error occured...")
      }else{
        setShow(true)
      }
    }

    const renderForm = () =>{
      if(show){
        return(
          <CreateQuestionForm examName={examName}/>
        )
       }else{
        return(
          <Container>
          <Form className="registerform">
            <h1>Set Examination Name</h1>
            <h5>Please set a unique Examination name</h5>
            <Form.Group className="mb-3" controlId="formBasicName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="enter name"
                name='name'
                value={examName}
                onChange={handleChange}
              />
            </Form.Group>
            <ButtonGroup className="mb-2">
         {radios.map((radio, idx) => (
          <ToggleButton
            key={idx}
            id={`radio-${idx}`}
            type="radio"
            variant="secondary"
            name="radio"
            value={radio.value}
            checked={radioValue === radio.value}
            onChange={(e) => setRadioValue(e.currentTarget.value)}
          >
            {radio.name}
          </ToggleButton>
        ))}
      </ButtonGroup>                        
  
            <Button variant="primary" onClick={examHandler}>
              Next
            </Button>
          </Form>
        </Container>
        );
       }
    }

  return (
    <>
       {renderForm()}
    </>
  )
}

export default CreateExam
