import React, { useEffect, useState } from 'react';
import { Button, Table } from 'react-bootstrap';
import { getAllExams } from '../../Actions/examAction';
import { useSelector, useDispatch } from 'react-redux';
import Loader from '../Loader';
import Error from '../Error';
import { useNavigate } from 'react-router-dom';


function UserHome() {
  const dispatch = useDispatch();
  const examState = useSelector((state) => state.getExamReducer);
  const { loading, error, Allexam } = examState;
  const navigate = useNavigate();

  const [results, setResults] = useState({});

  const fetchResult = async (exam_id) => {
    const res = await fetch(`/api/result/getResultByUserId?cacheBuster=${Date.now()}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ exam_id }),
    }).then((data) => data.json());

    // console.log(res[0].percentage)
    return res[0]?.percentage ?? null;
  };

  useEffect(() => {
    dispatch(getAllExams());
  }, [dispatch]);

  useEffect(() => {
    if (Allexam) {
      Allexam.forEach((exam) => {
        fetchResult(exam._id)
          .then((result) => {
            setResults((prevState) => ({
              ...prevState,
              [exam._id]: result,
            }));
          })
          .catch((error) => {
            console.error('Error fetching result:', error);
          });
      });
    }
  }, [Allexam]);
 console.log(results)
  return (
    <>
      <div style={{ padding: '6rem' }}>
        {loading && <Loader />}
        {error && <Error error="Error While Fetching Users" />}
        <Table>
          <thead>
            <tr>
              <th>Exam Name</th>
              <th>Teacher Name</th>
              <th>Exam Date</th>
              <th>Start Exam</th>
              <th>Result</th>
            </tr>
          </thead>
          <tbody>
            {Allexam &&
              Allexam.map((exam) => (
                <tr key={exam._id}>
                  <td>{exam.exam_name}</td>
                  <td>{exam.teacher_name}</td>
                  <td>{exam.exam_date}</td>
                  <td>
                    {results[exam._id] === null ? <Button onClick={() => navigate(`/examination/${exam._id}`)}>Start</Button> : "Completed"}
                  </td>
                  <td>{results[exam._id] === null ? 'Null' : results[exam._id]}</td>
                </tr>
              ))}
          </tbody>
        </Table>
      </div>
    </>
  );
}

export default UserHome;
