import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import UserHome from '../Components/User/UserHome';

function HomeScreen() {
  const data = sessionStorage.getItem('userData');
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (data) {
      setCurrentUser(JSON.parse(data));
    }
  }, [data]);

  useEffect(() => {
    const handleBeforeUnload = (event) => {
      event.preventDefault();
      event.returnValue = '';
    };

    const handleNavigate = (location, action) => {
      if (action === 'POP') {
        const confirmed = window.confirm('Are you sure you want to exit the website?');
        if (confirmed) {
          sessionStorage.clear();
          window.location.href = '/';
        } else {
          navigate('/');
        }
      }
    };

    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, [navigate]);

  return (
    <div>
      {currentUser !== null ? (
        <div>
          {currentUser.isAdmin === true ? (
            <h4>
              Go To Admin Panel <Link to="/admin/dashboard">click me!</Link>
            </h4>
          ) : (
            <UserHome />
          )}
        </div>
      ) : (
        <div>
          <h1>Welcome to VSSUT, MCA Exam Portal</h1>
          <h4>Please Login to continue...</h4>
        </div>
      )}
    </div>
  );
}

export default HomeScreen;
