import React, { useState } from 'react';
import { Button } from 'react-bootstrap';

const CreateQuestionForm = () => {
  
    const [questions , setQuestions] = useState([
        {
            title: "",
            options:["","","",""],
            answerIndex:0
        }
    ]);

    const handleQuestionChange = (index,field,value) =>{
        const updatedQuestion = [...questions];
        updatedQuestion[index][field] = value
        setQuestions(updatedQuestion);
    }

    const handleOptionChange = (index,optionIndex,value) => {
        const updatedQuestion = [...questions];
        updatedQuestion[index].options[optionIndex] = value;
        setQuestions(updatedQuestion)
    }

    const handleDeleteQuestion = (index) =>{
        const updatedQuestion = [...questions];
        updatedQuestion.splice(index,1);
        setQuestions(updatedQuestion);
    }

    const handleAddQuestions = () => {
        const newQuestion = {
            title : "",
            options: ["","","",""],
            answerIndex: 0
        }

        setQuestions([...questions,newQuestion]);
    }

    const formSubmit = async(e) =>{
        e.preventDefault();

        
    }

  return (
    <>
        <div>
        <h1></h1>
            {questions.map((question,index)=>(
                <div key={index}>
                <h2>Question {index+1}</h2>
               <input 
                    value={question.title}
                    type='text'
                    placeholder='Question Title'
                    onChange={(e)=> handleQuestionChange(index,'title',e.target.value)}
               />
               <h4>Options:</h4>
              {question.options.map((option,optionIndex)=>(
                <div key={optionIndex}>
                <input 
                    type='text'
                    value={option} 
                    onChange={(e)=>handleOptionChange(index,optionIndex,e.target.value) } 
                    placeholder= {`Option ${optionIndex+1}`}
                />
                </div>
                ))}
            <h4>Answer:</h4>
            <select
                value={question.answerIndex}
                onChange={(e)=>handleQuestionChange(index,'answerIndex',parseInt(e.target.value))}>
                {question.options.map((_,optionIndex)=>(
                    <option key={optionIndex} value={optionIndex} >
                        Option {optionIndex+1}
                    </option>
                ))}
            </select>
            <Button onClick={()=>handleDeleteQuestion(index)}>DELETE</Button>
                </div>
            ))}
            <Button onClick={handleAddQuestions}>ADD</Button>
            <Button onClick={formSubmit}>Submit</Button>
        </div>
    </>
  );
};

export default CreateQuestionForm;
