import {legacy_createStore as createStore,combineReducers,applyMiddleware} from 'redux'
import thunk from 'redux-thunk'
import {composeWithDevTools} from 'redux-devtools-extension'
import { registerUserReducer,loadingReducer,getAllUserReducer,loginUserReducer,logoutUserReducer,authReducer} from './Reducers/userReducer'
import Cookies from 'js-cookie'


const user = Cookies.get('jwtoken') !== undefined ? true : false;
const token = Cookies.get('jwtoken')
console.log(token)
const rootReducer = combineReducers({
    registerUserReducer: registerUserReducer,
    loadingReducer: loadingReducer,
    getAllUserReducer: getAllUserReducer,
    loginUserReducer: loginUserReducer,
    logoutUserReducer : logoutUserReducer,
    authReducer : authReducer,
})

const initilState = {
    // loginUserReducer:{
    //     token: token
    // },
    authReducer:{
        isAuthenticated: user
    }
}

const middleware = [thunk]

const store = createStore(rootReducer,initilState,composeWithDevTools(applyMiddleware(...middleware)));
export default store;
