import React,{useEffect, useState} from 'react'
import { Button,Modal, Row } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux';
import { getAllQuestions } from '../../Actions/examAction';

function ResultData({student,percentage,exam_id,answers}) {
    const dispatch = useDispatch();
    const QuestionsData = useSelector((state)=> state.getAllQuestionsReducer)
    const {Questions,loading,error} = QuestionsData;
    // console.log(student)
    //  console.log(Questions[0].questions)
    console.log(answers)
    const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(()=>{
    dispatch(getAllQuestions(exam_id));
  },[exam_id,dispatch])
  return (
    <>
       <tr>
          <td>{student.name}</td>
          <td>{student.registration_no}</td>
          <td>{answers[0]}</td>
          <td><Button onClick={handleShow}>Review</Button></td>
        </tr>

        <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {Questions && Questions[0].questions.map((question,index)=>(
            <div key={index}>
              <Row>
                <h4>({index+1}): {question.title}</h4>
                {question.options.map((option,optionIndex)=>(
                   <div>
                    { answers.map((answer,answerIndex)=>{
                      if(answer===optionIndex && answer===question.answer){
                       return <h5 style={{color:"green"}}>{option}</h5>
                      }else if(answer===optionIndex){
                        return (<div>
                          <h5 key={question.answer} style={{color:"green"}}>{option}</h5>
                          <h5 style={{color:"red"}} key={answerIndex}>{option}</h5>
                        </div>)
                      }
                      else{
                        return <h5>{option}</h5>
                      }
                    })
                    }
                    
                   </div>
                )                 
                )}
              </Row>
            </div>
          ))}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  )
}

export default ResultData
