import React, { useEffect, useState } from 'react'
import {Navbar,Container,Nav,NavDropdown} from 'react-bootstrap'
import {useSelector,useDispatch} from 'react-redux'
import {LinkContainer} from 'react-router-bootstrap'
import { logoutUser,getRootUser } from '../Actions/userAction'
import {FaUserAlt} from 'react-icons/fa'
function NavBar() {
  const dispatch = useDispatch();
    const authState = useSelector((state)=>state.authReducer);
    const {isAuthenticated} = authState;
    const loginstate= useSelector((state)=>state.loginUserReducer);
    const {token} = loginstate;
      const [currentUser,setCurrentUser] = useState();
  //  console.log(token)
  console.log(currentUser)
  const userData = async()=>{
    const res = await fetch('/api/user/rootUser',{
      method:"GET"
    }).then((r)=>{return r.json()}).catch(err=>console.log(err))
    setCurrentUser(res)
  }

  useEffect(()=>{
    // console.log(token)
    if(token){
      userData();
    }
  },[token])
  return (
    <>
         <Navbar bg="light" expand="lg">
      <Container>
        <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            {
                isAuthenticated ? (
                    <div>
                      {currentUser == null ? (
                        <div>loading...</div>
                      ):(
                        <div>
                      <LinkContainer to="/">
                      {/* <Nav.Link href="#features">{currentUser.name}</Nav.Link> */}
                        <NavDropdown title={<FaUserAlt/>} id="basic-nav-dropdown">
                          <h6>{currentUser.name}</h6>
                            <NavDropdown.Item>
                              profile
                            </NavDropdown.Item>
                            <NavDropdown.Item
                              onClick={() => {
                                dispatch(logoutUser());
                              }}
                            >
                              Logout
                            </NavDropdown.Item>
                          </NavDropdown>
                      </LinkContainer>
                    </div>
                      )}
                    </div>
                ):(
                    <LinkContainer to="/login">
                    <Nav.Link href="#features">Login</Nav.Link>
                    </LinkContainer>
                )
            }
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
    </>
  )
}

export default NavBar
